mapper.select:
SELECT SUB_CA_NAME
FROM SUB_CA
WHERE SUB_CA_NO = #{param1}

SELECT NVL(COUNT(REPLY_NO), 0)
FROM Q, REPLY R
WHERE Q.Q_NO = R.Q_NO
AND Q.Q_NO = #{param1}
AND Q.SUB_CA_NO = #{param2}

mapper.update:
UPDATE Q
SET Q_RPT_CNT = +1
WHERE Q_NO = #{param1}

UPDATE Q
SET Q_EXP=#{param2}, Q_SOS = Q_SOS + 1
WHERE Q_NO = #{param1}

UPDATE REPLY
SET REPLY_UP = REPLY_UP+1
WHERE REPLY_NO=#{param1}

UPDATE REPLY
SET REPLY_PICK = REPLY_PICK+1
WHERE REPLY_NO = #{param1}

UPDATE REPLY
SET REPLY_RPT_CNT = REPLY_RPT_CNT + 1
WHERE REPLY_NO = #{param1}
